// Get the clientId from query params
const clientId = $input.first().json.query.clientId;

// Find the matching client from all rows
const allClients = $input.all();
const client = allClients.find(item => item.json.clientId === clientId);

if (!client) {
  return { json: { error: 'Client not found', clientId } };
}

// Parse JSON fields back to objects
const data = { ...client.json };

try {
  if (data.platformStatuses) data.platformStatuses = JSON.parse(data.platformStatuses);
} catch(e) { data.platformStatuses = {}; }

try {
  if (data.additionalUsers) data.additionalUsers = JSON.parse(data.additionalUsers);
} catch(e) { data.additionalUsers = []; }

try {
  if (data.avatarPeople) data.avatarPeople = JSON.parse(data.avatarPeople);
} catch(e) { data.avatarPeople = []; }

// Convert platforms string back to array
if (data.platforms && typeof data.platforms === 'string') {
  data.platforms = data.platforms.split(', ').filter(p => p);
}

// Convert addons string back to array
if (data.addons && typeof data.addons === 'string') {
  data.addons = data.addons.split(', ').filter(a => a);
}

// Convert otherPhotosUrls string back to array
if (data.otherPhotosUrls && typeof data.otherPhotosUrls === 'string') {
  data.otherPhotosUrls = data.otherPhotosUrls.split(', ').filter(u => u);
}

return { json: { client: data } };
