// Generate unique client ID
const now = new Date();
const year = now.getFullYear().toString().slice(-2);
const month = String(now.getMonth() + 1).padStart(2, '0');
const random = Math.floor(Math.random() * 1000).toString().padStart(3, '0');
const clientId = `ADP-${year}${month}-${random}`;

const body = $input.first().json.body;

// Flatten data for Google Sheets
return {
  json: {
    clientId,
    timestamp: body.timestamp,

    // Business Info
    businessName: body.businessName,
    businessAddress: body.businessAddress,
    businessPhone: body.businessPhone,
    websiteUrl: body.websiteUrl || '',
    socialHandle: body.socialHandle || '',
    industry: body.industry,

    // Platforms
    platforms: body.platforms.join(', '),
    platformStatuses: JSON.stringify(body.platformStatuses),
    platformEmail: body.platformEmail,
    platformNotes: body.platformNotes || '',

    // Portal Access
    primaryContactName: body.primaryContactName,
    primaryContactEmail: body.primaryContactEmail,
    primaryContactPhone: body.primaryContactPhone || '',
    additionalUsers: JSON.stringify(body.additionalUsers),

    // Verification Docs
    ownerName: body.ownerName,
    businessLicenseUrl: body.businessLicenseUrl || '',
    ownerIdFrontUrl: body.ownerIdFrontUrl || '',
    ownerIdBackUrl: body.ownerIdBackUrl || '',

    // Brand Assets
    logoUrl: body.logoUrl || '',
    brandColors: body.brandColors || '',
    tagline: body.tagline || '',
    exteriorPhotoUrl: body.exteriorPhotoUrl || '',
    interiorPhotoUrl: body.interiorPhotoUrl || '',
    otherPhotosUrls: Array.isArray(body.otherPhotosUrls) ? body.otherPhotosUrls.join(', ') : '',

    // Avatar People
    avatarPeople: JSON.stringify(body.avatarPeople),

    // Plan & Pricing
    selectedPlan: body.selectedPlan,
    addons: body.addons.join(', '),
    onboardingComplexity: body.onboardingComplexity,
    onboardingTotal: body.onboardingTotal,

    // Notes
    meetingNotes: body.meetingNotes || '',
    followUpItems: body.followUpItems || '',
    redFlags: body.redFlags || ''
  }
};
